mapper.select:
select * from SALES_ORDER_C

	TENANT_ID = #{tenantId}
	 AND SALES_ORDER_NO = #{orderNo}
	AND to_char(SALE_TIME,'yyyy-MM') = #{saleTime}

mapper.update:
update SALES_ORDER_C
SALES_ORDER_NO = #{salesOrderNo}, CUSTOM_NO = #{customNo}, CUSTOM_NAME = #{customName}, PRODUCT_NO = #{productNo}, PRODUCT_NAME = #{productName}, PRODUCT_AOMOUT = #{productAmount}, PACKING_UNIT = #{packingUnit}, SALE_TIME = #{saleTime}, VALID_START_TIME = #{validStartTime}, VALID_END_TIME = #{validEndTime},
where SALES_ORDER_ID = #{id}

mapper.delete:
delete from SALES_ORDER_C 
where SALES_ORDER_ID in

	#{id}
