mapper.select:
select * from PRODUCT_C

	TENANT_ID = #{tenantId}
	 AND PRODUCT_NO = #{productNo}

mapper.update:
update PRODUCT_C
PRODUCT_NAME = #{name}, PRODUCT_NO = #{productNo}, BAR_CODE = #{barCode}, SPECIFICATION = #{specification}, PACKING_UNIT = #{packingUnit}, PRICE = #{price}, DESCRIPTION = #{description}, MEMO = #{memo},
where PRODUCT_ID = #{id}

mapper.delete:
delete from PRODUCT_C 
where PRODUCT_ID in

	#{id}
