mapper.select:
select * from STOCK_C

	TENANT_ID = #{tenantId}
	 AND materialNo = #{materialNo}

mapper.update:
update STOCK_C
REPOSITORY_NO = #{repositoryNo}, GOODS_NO = #{goodsNo}, GOODS_NAME = #{goodsName}, GOODS_TYPE = #{goodsType}, AMOUNT = #{amount}, PACKING_UNIT = #{packingUnit},
where STOCK_ID = #{id}

mapper.delete:
delete from STOCK_C 
where STOCK_ID in

	#{id}

mapper.update:
update STOCK_C
set AMOUNT = #{amount}
where TENANT_ID = #{tenantId} AND GOODS_NO = #{goodNo} and REPOSITORY_NO = #{repositoryNo}
