mapper.select:
select * from MATERIAL_C

	TENANT_ID = #{tenantId}
	 AND MATERIAL_NO = #{materialNo}

mapper.update:
update MATERIAL_C
MATERIAL_NO = #{materialNo}, MATERIAL_NAME = #{name}, SPECIFICATION = #{specification}, PACKING_UNIT = #{packingUnit}, PRICE = #{price}, MEMO = #{memo},
where MATERIAL_ID = #{id}

mapper.delete:
delete from MATERIAL_C 
where MATERIAL_ID in

	#{id}
