mapper.select:
select * from MATERIAL_OUT_C

	TENANT_ID = #{tenantId}
	 AND state = #{state}

mapper.update:
update MATERIAL_OUT_C
MATERIAL_NO = #{materialNo}, MATERIAL_NAME = #{materialName}, MATERIAL_AMOUNT = #{materialAmount}, PACKING_UNIT = #{packingUnit}, PICKING_PEOPLE = #{pickingPeople}, OPERATOR = #{operator}, REPOSITORY_NO = #{repositoryNo}, OUT_TIME = #{outTime}, STATE = #{state}, MEMO = #{memo},
where MATERIAL_OUT_ID = #{id}

mapper.delete:
delete from MATERIAL_OUT_C 
where MATERIAL_OUT_ID in

	#{id}

mapper.update:
update MATERIAL_OUT_C
set STATE = ${state}
where MATERIAL_OUT_ID in 

	#{id}
