mapper.update:
update PRODUCT_OUT_C
PRODUCT_NO = #{productNo}, PRODUCT_NAME = #{productName}, PRODUCT_AMOUNT = #{productAmount}, PACKING_UNIT = #{packingUnit}, OPERATOR = #{operator}, REPOSITORY_NO = #{repositoryNo}, Out_TIME = #{outTime}, STATE = #{state}, MEMO = #{memo},
where PRODUCT_OUT_ID = #{id}

mapper.delete:
delete from PRODUCT_OUT_C 
where PRODUCT_OUT_ID in

	#{id}

mapper.update:
update PRODUCT_OUT_C
set STATE = ${state}
where PRODUCT_OUT_ID in 

	#{id}
