mapper.select:
select * from TENANT_C

	TENANT_NO = #{tenantNo}	
	AND PASSWORD = #{password}

mapper.update:
update TENANT_C
TENANT_NO = #{tenantNo}, PASSWORD = #{password}, NAME = #{name}, COMPANY = #{company}, CONTACTOR = #{contactor}, ADDRESS = #{address}, TELEPHONE = #{TELEPHONE}, STATE = #{state},
where TENANT_ID = #{id}

mapper.delete:
delete from TENANT_C 
where TENANT_ID in

	#{id}

mapper.update:
update TENANT_C
set STATE = ${state}
where TENANT_ID in 

	#{id}
